
--- 中类销售维度
 SELECT
	SUBSTRING(sdt, 1, 6) mon ,
	case when channel in ('5','6') and a.customer_no like 'S%' then '商超' when channel in ('5','6') and a.customer_no not like 'S%' then '大客户' else channel_name end channel_sale,
	if(is_self_sale=1,'自营','联营') is_parter ,
	province_name,
	province_code,
	goods_code ,
	goods_name ,
	division_code ,
	division_name ,
	category_large_code ,
	category_large_name ,
	category_middle_code ,
	category_middle_name,
	category_small_code,
	category_small_name,
	SUM(sales_qty)sales_qty,
	SUM(sales_value)sale,
	SUM(sales_cost )sales_cost,
	SUM(profit)profit,
	sum(case when report_price =1 then  purchase_price * sales_qty end  )as purchase_sale,
	sum(case when report_price =1 then middle_office_price *sales_qty end )as middle_sale,
	-- sum(sales_value -front_profit )as middle_cost,
	sum(front_profit )as front_profit 
FROM
	csx_dw.dws_sale_r_d_customer_sale a
WHERE
	sdt >= '20200701'
	AND sdt <= '20200731'
GROUP BY
	SUBSTRING(sdt, 1, 6),
	if(is_self_sale=1,'自营','联营'),
	case when channel in ('5','6') and a.customer_no like 'S%' then '商超'  when channel in ('5','6') and a.customer_no not like 'S%' then '大客户' else channel_name end,
	province_name,
	province_code,
	division_code ,
	division_name ,
	category_large_code ,
	category_large_name ,
	category_middle_code ,
	category_middle_name,
	category_small_code,
	category_small_name,
	goods_code ,
	goods_name ;


