--月份	渠道	省区	部类编码	部类名称	课组编码	课组名称	品项数	销额	毛利额

SELECT
	mon,
	channel_name,
	province_name,
	province_code,
	bd_id,
	bd_name,
	dept_id,
	dept_name,
	COUNT(DISTINCT goods_code) as goods_sku,
	sum(sales_qty)sales_qty,
	sum(sale)sale,
	sum(sales_cost)sales_cost,
	sum(profit) profit
FROM
(
SELECT
	SUBSTRING(a.sdt, 1, 6) mon ,
	channel_name,
	case when dc_code ='W0H4' then dc_name else  province_name end province_name,
	case when dc_code ='W0H4' then dc_code else  province_code end province_code,
	customer_no,
	customer_name,
	first_category,
	second_category,
	vendor_code,
	vendor_name,
	goods_code,
	goods_name,
	unit,
	bd_id,
	bd_name,
	firm_code,
	firm_name,
	brand_name,
	department_code dept_id,
	department_name dept_name,
	category_large_code ,
	category_large_name ,
	category_middle_code ,
	category_middle_name,
	a.category_small_code,
	category_small_name,
	SUM(sales_qty)sales_qty,
	SUM(sales_value)sale,
	SUM(sales_cost)sales_cost,
	SUM(profit)profit
FROM
	csx_dw.dws_sale_r_d_customer_sale a 
	join 
	(select category_small_code ,'' firm_code,'' firm_name,g.business_division_code bd_id, g.business_division_name bd_name from csx_dw.dws_basic_w_a_category_m as g where sdt='current' ) b 
	   on a.category_small_code =b.category_small_code
WHERE
	a.sdt >= '20200601'
	AND a.sdt <= '20200602'
	--AND category_small_code >='12000000' and category_small_code <='14999999'
GROUP BY
	SUBSTRING(a.sdt, 1, 6),
	channel_name,
	province_name ,
	province_code ,
	customer_no,
	customer_name,
	vendor_code,
	vendor_name,
	goods_code,
	goods_name,
	unit,
	firm_code,
	firm_name,
	brand_name,
	bd_id,
	bd_name,
	department_code,
	department_name,
	category_large_code ,
	category_large_name ,
	category_middle_code ,
	category_middle_name,
	category_small_code,
	category_small_name,
	first_category,
	second_category )a 
	group by 
	mon,
	channel_name,
	province_name,
	province_code,	
	bd_id,
	bd_name,
	dept_id,
	dept_name
;

